import React, { useState } from 'react'
import { Box, Stack, TextField, Typography } from '@mui/material';

const Login = () => {
    const [id, setId] = useState('');
    const [pwd, setPwd] = useState('');

    const [idMsg, setIdMsg] = useState('아이디를 입력해주세요.');
    const [pwdMsg, setPwdMsg] = useState('비밀번호를 입력해주세요.');

    return (
        <Box sx={{ 
            width: '600px',
            height: '800px'
         }}>
            <Typography variant='h3' component="h3">로그인</Typography>
            <Stack gap={6}>
                <TextField sx={{
                    '& .MuiInputBase-root': {
                        "&:hover": {
                            color: 'white'
                        }
                    },
                    width: '60%'
                }} onChange={(e) => setId(e.target.value)} id='outlined-basic' variant='outlined' placeholder='아이디' value={id}></TextField>
                {
                id != '' 
                ? <label></label>
                : <label>{idMsg}</label>
                }
                <TextField sx={{
                    '& .MuiInputBase-root': {
                        "&:hover": {
                            color: 'white'
                        }
                    },
                    width: '60%'
                }} onChange={(e) => setPwd(e.target.value)} id='outlined-basic' variant='outlined' placeholder='비밀번호' value={pwd}></TextField>
            </Stack>
        </Box >
    )
}

export default Login;
